// src/api/authApi.js
import api from './axios';

export const authApi = {
  signup: (dto) => api.post('/signup', dto).then((r) => r.data),
  verifyEmail: (dto) => api.post('/verify-email', dto).then((r) => r.data),
  resendCode: (email) => api.post('/resend-code', { email }).then((r) => r.data),
  createPassword: (dto) => api.post('/create-password', dto).then((r) => r.data),
  login: (dto) => api.post('/login', dto).then((r) => r.data),
  forgotPassword: (email) => api.post('/forgot-password', { email }).then((r) => r.data),
  resetPassword: (dto) => api.post('/reset-password', dto).then((r) => r.data),
   createPin: (dto, token) => 
    api.post('/create-pin', dto, {
      headers: {
        Authorization: `Bearer ${token}`, // pass JWT token
      },
    }).then((r) => r.data),

};
