import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import DarkModeToggle from './DarkModeToggle';

const AddRecipient = () => {
  const navigate = useNavigate();
  const { logout } = useAuth();
  const [formData, setFormData] = useState({
    fullName: '',
    bank: '',
    accountNumber: '',
    swiftCode: '',
    address: '',
    state: '',
    city: '',
    zipCode: '',
    phoneNumber: '',
    email: ''
  });
  const [errors, setErrors] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [hasAnimated, setHasAnimated] = useState(false);

  const banks = [
    'Access Bank',
    'First Bank',
    'GTBank',
    'Lead Bank',
    'UBA',
    'Zenith Bank',
    'Choose Bank'
  ];

  useEffect(() => {
    const timer = setTimeout(() => setHasAnimated(true), 100);
    return () => clearTimeout(timer);
  }, []);

  const handleInputChange = (field, value) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    // Clear error when user starts typing
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: '' }));
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const validateForm = () => {
    const newErrors = {};
    const requiredFields = ['fullName', 'bank', 'accountNumber', 'swiftCode', 'address', 'state', 'city', 'zipCode', 'phoneNumber', 'email'];

    requiredFields.forEach(field => {
      if (!formData[field] || formData[field] === 'Choose Bank') {
        newErrors[field] = 'This field is required';
      }
    });

    // Email validation
    if (formData.email && !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      newErrors.email = 'Please enter a valid email address';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }

    setIsLoading(true);
    
    // Simulate API call
    setTimeout(() => {
      setIsLoading(false);
      // In a real app, you'd save the recipient data to state/context
      console.log('Saved recipient:', formData);
      navigate('/recipients');
    }, 1500);
  };

  const renderInputField = (label, field, type = 'text', placeholder = '') => (
    <div className="space-y-2">
      <label className="text-neutral-dark dark:text-dark-text font-medium">
        {label} <span className="text-error">*</span>
      </label>
      <input
        type={type}
        value={formData[field]}
        onChange={(e) => handleInputChange(field, e.target.value)}
        placeholder={placeholder}
        className={`w-full px-4 py-3 rounded-lg border ${
          errors[field] ? 'border-error' : 'border-neutral-lightgray dark:border-dark-border'
        } bg-white dark:bg-dark-card text-neutral-dark dark:text-dark-text placeholder:text-neutral-placeholder focus:border-primary-blue focus:ring-4 focus:ring-primary-blue/10 transition-all duration-200`}
        disabled={isLoading}
      />
      {errors[field] && (
        <p className="text-error text-xs animate-slide-in-down animate-once">{errors[field]}</p>
      )}
    </div>
  );

  return (
    <div className="min-h-screen bg-white dark:bg-dark-bg transition-colors duration-300">
      {/* Header */}
      <header className={`flex justify-between items-center px-4 lg:px-20 py-4 lg:py-6 ${hasAnimated ? 'animate-slide-in-down animate-once' : 'opacity-0'}`}>
        {/* Logo */}
        <div className="flex items-center">
          <div className="w-10 h-7 lg:w-13 lg:h-9 mr-3">
            <svg width="52" height="37" viewBox="0 0 52 37" fill="none" className="w-full h-full">
              <rect width="52" height="37" fill="#6C63FF" />
            </svg>
          </div>
          <div className="text-black/80 dark:text-dark-text font-times text-lg lg:text-2xl font-bold transition-colors duration-300">
            FLOWWAVE
          </div>
        </div>

        {/* Header Actions */}
        <div className="flex items-center gap-4 lg:gap-10">
          {/* Customer Service */}
          <button className="p-2 rounded-full hover:bg-gray-100 dark:hover:bg-dark-surface transition-all duration-200">
            <svg width="40" height="40" viewBox="0 0 50 50" fill="none" className="w-8 h-8 lg:w-10 lg:h-10">
              <path d="M35.416 24.5924C35.416 23.8722 35.416 23.5122 35.5243 23.1913C35.8393 22.2588 36.6698 21.8972 37.5016 21.5182C38.4368 21.0922 38.9043 20.8793 39.3677 20.8418C39.8937 20.7992 40.4206 20.9126 40.8702 21.1649C41.4662 21.4993 41.8818 22.1349 42.3075 22.6518C44.2729 25.0388 45.2554 26.2326 45.615 27.5488C45.9052 28.6109 45.9052 29.7218 45.615 30.7838C45.0906 32.7036 43.4337 34.313 42.2073 35.8026C41.58 36.5645 41.2662 36.9455 40.8702 37.1678C40.4206 37.4201 39.8937 37.5334 39.3677 37.4909C38.9043 37.4534 38.4368 37.2405 37.5016 36.8145C36.6698 36.4355 35.8393 36.0738 35.5243 35.1413C35.416 34.8205 35.416 34.4605 35.416 33.7403V24.5924Z" stroke="#3A49A4" strokeWidth="3"/>
              <path d="M14.5827 24.5925C14.5827 23.6859 14.5572 22.8709 13.8242 22.2334C13.5575 22.0015 13.204 21.8404 12.4971 21.5184C11.5619 21.0925 11.0943 20.8796 10.6309 20.8421C9.24083 20.7296 8.49293 21.6784 7.69129 22.6521C5.72591 25.0392 4.74322 26.2327 4.38364 27.549C4.09347 28.6113 4.09347 29.7221 4.38364 30.7842C4.9081 32.704 6.56502 34.3132 7.79145 35.8029C8.56454 36.7419 9.30304 37.5986 10.6309 37.4913C11.0943 37.4538 11.5619 37.2407 12.4971 36.8148C13.204 36.4927 13.5575 36.3317 13.8242 36.0998C14.5572 35.4623 14.5827 34.6475 14.5827 33.7407V24.5925Z" stroke="#3A49A4" strokeWidth="3"/>
              <path d="M41.6673 21.8753V18.7503C41.6673 10.6962 34.2054 4.16699 25.0007 4.16699C15.7959 4.16699 8.33398 10.6962 8.33398 18.7503V21.8753" stroke="#3A49A4" strokeWidth="3" strokeLinecap="round" strokeLinejoin="round"/>
              <path d="M41.6667 36.458C41.6667 45.833 33.3333 45.833 25 45.833" stroke="#3A49A4" strokeWidth="3" strokeLinecap="round" strokeLinejoin="round"/>
            </svg>
          </button>

          {/* Notifications */}
          <button className="p-2 rounded-full hover:bg-gray-100 dark:hover:bg-dark-surface transition-all duration-200">
            <svg width="40" height="40" viewBox="0 0 50 50" fill="none" className="w-8 h-8 lg:w-10 lg:h-10">
              <path d="M32.2923 37.5C32.2923 41.5271 29.0277 44.7917 25.0007 44.7917C20.9736 44.7917 17.709 41.5271 17.709 37.5" stroke="#3A49A4" strokeWidth="3" strokeLinecap="round" strokeLinejoin="round"/>
              <path d="M40.0648 37.4997H9.93515C7.8999 37.4997 6.25 35.8497 6.25 33.8145C6.25 32.8372 6.63825 31.8999 7.32935 31.2086L8.58608 29.952C9.75819 28.7799 10.4167 27.1901 10.4167 25.5326V19.7913C10.4167 11.7372 16.9459 5.20801 25 5.20801C33.0542 5.20801 39.5833 11.7372 39.5833 19.7913V25.5326C39.5833 27.1901 40.2419 28.7799 41.414 29.952L42.6706 31.2086C43.3617 31.8999 43.75 32.8372 43.75 33.8145C43.75 35.8497 42.1 37.4997 40.0648 37.4997Z" stroke="#3A49A4" strokeWidth="3" strokeLinecap="round" strokeLinejoin="round"/>
            </svg>
          </button>

          {/* Dark Mode Toggle */}
          <DarkModeToggle />

          {/* Logout Button */}
          <button
            onClick={handleLogout}
            className="px-4 py-2 lg:px-6 lg:py-3 border-2 border-primary-blue text-primary-blue rounded-lg font-bold text-sm lg:text-lg hover:bg-primary-blue hover:text-white transition-all duration-300"
          >
            Log Out
          </button>
        </div>
      </header>

      {/* Main Content */}
      <main className="flex flex-col items-center px-4 lg:px-0 pb-24">
        <div className={`w-full max-w-lg ${hasAnimated ? 'animate-fade-in-up animate-once' : 'opacity-0'}`} style={{ animationDelay: '0.2s' }}>
          {/* Back Button */}
          <button
            onClick={() => navigate('/recipients')}
            className="flex items-center gap-2 mb-6 text-neutral-gray hover:text-neutral-dark transition-colors"
          >
            <svg width="20" height="20" viewBox="0 0 20 20" fill="none">
              <path d="M12.5 15L7.5 10L12.5 5" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
            </svg>
            <span className="text-sm font-medium">Back</span>
          </button>

          {/* Add Recipient Title */}
          <h1 className="text-center text-2xl lg:text-3xl font-bold text-primary-pink mb-8 lg:mb-10">
            Add Recipient
          </h1>

          {/* Form */}
          <form onSubmit={handleSubmit} className="space-y-6">
            {/* Personal Information */}
            <div className={`space-y-6 ${hasAnimated ? 'animate-slide-in-up animate-once' : 'opacity-0'}`} style={{ animationDelay: '0.4s' }}>
              {renderInputField('Full Name', 'fullName', 'text', 'First Name')}
              
              {/* Bank Dropdown */}
              <div className="space-y-2">
                <label className="text-neutral-dark dark:text-dark-text font-medium">
                  Bank <span className="text-error">*</span>
                </label>
                <div className="relative">
                  <select
                    value={formData.bank}
                    onChange={(e) => handleInputChange('bank', e.target.value)}
                    className={`w-full px-4 py-3 rounded-lg border ${
                      errors.bank ? 'border-error' : 'border-neutral-lightgray dark:border-dark-border'
                    } bg-white dark:bg-dark-card text-neutral-dark dark:text-dark-text appearance-none focus:border-primary-blue focus:ring-4 focus:ring-primary-blue/10 transition-all duration-200`}
                    disabled={isLoading}
                  >
                    <option value="">Choose Bank</option>
                    {banks.filter(bank => bank !== 'Choose Bank').map(bank => (
                      <option key={bank} value={bank}>{bank}</option>
                    ))}
                  </select>
                  <svg className="absolute right-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-neutral-dark dark:text-dark-text pointer-events-none" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                  </svg>
                </div>
                {errors.bank && (
                  <p className="text-error text-xs animate-slide-in-down animate-once">{errors.bank}</p>
                )}
              </div>

              {renderInputField('Account Number', 'accountNumber', 'text', 'Address')}
              {renderInputField('Swift/Sort Code', 'swiftCode', 'text', 'Enter Swift/Sort Code')}
            </div>

            {/* Address Information */}
            <div className={`space-y-6 ${hasAnimated ? 'animate-slide-in-up animate-once' : 'opacity-0'}`} style={{ animationDelay: '0.6s' }}>
              {renderInputField('Address', 'address', 'text', 'Address')}
              {renderInputField('State', 'state', 'text', 'State')}
              {renderInputField('City', 'city', 'text', 'City')}
              {renderInputField('Zip Code', 'zipCode', 'text', 'Zip Code')}
            </div>

            {/* Contact Information */}
            <div className={`space-y-6 ${hasAnimated ? 'animate-slide-in-up animate-once' : 'opacity-0'}`} style={{ animationDelay: '0.8s' }}>
              {renderInputField('Phone Number', 'phoneNumber', 'tel', 'Phone Number')}
              {renderInputField('Email', 'email', 'email', 'Email Address')}
            </div>

            {/* Disclaimer */}
            <div className={`p-4 bg-secondary-100 rounded-lg ${hasAnimated ? 'animate-fade-in animate-once' : 'opacity-0'}`} style={{ animationDelay: '1s' }}>
              <div className="flex items-start gap-3">
                <svg width="24" height="24" viewBox="0 0 24 24" fill="none" className="text-neutral-dark dark:text-dark-text flex-shrink-0">
                  <path d="M11.9117 15.7636V11.8752M11.9117 7.98686H11.9216M21.838 11.8752C21.838 17.244 17.3938 21.5962 11.9117 21.5962C6.42951 21.5962 1.98535 17.244 1.98535 11.8752C1.98535 6.50651 6.42951 2.1543 11.9117 2.1543C17.3938 2.1543 21.838 6.50651 21.838 11.8752Z" stroke="currentColor" strokeWidth="1.6" strokeLinecap="round" strokeLinejoin="round"/>
                </svg>
                <p className="text-neutral-dark dark:text-dark-text text-xs leading-relaxed">
                  To continue this payment, you confirm that to the best of your knowledge, the details you're providing is correct.
                </p>
              </div>
            </div>

            {/* Save Button */}
            <button
              type="submit"
              className={`w-full py-4 bg-primary-blue text-white text-lg font-bold rounded-lg hover:bg-primary-blue/90 hover:-translate-y-1 hover:shadow-lg transition-all duration-300 ${isLoading ? 'opacity-50 cursor-not-allowed' : ''} ${hasAnimated ? 'animate-slide-in-up animate-once' : 'opacity-0'}`}
              style={{ animationDelay: '1.2s' }}
              disabled={isLoading}
            >
              {isLoading ? (
                <div className="flex items-center justify-center gap-2">
                  <div className="w-5 h-5 border-2 border-white/30 border-t-white rounded-full animate-spin"></div>
                  Saving...
                </div>
              ) : (
                'Save & Continue'
              )}
            </button>
          </form>
        </div>
      </main>

      {/* Bottom Navigation */}
      <nav className={`fixed bottom-0 left-0 right-0 bg-white dark:bg-dark-surface shadow-soft dark:shadow-dark-soft px-6 py-6 ${hasAnimated ? 'animate-slide-in-up animate-once' : 'opacity-0'}`} style={{ animationDelay: '1.4s' }}>
        <div className="flex justify-between items-center max-w-2xl mx-auto">
