import React, { createContext, useContext, useState, useEffect } from 'react';
import { authApi } from '../api/authApi';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Check if user is already logged in (from localStorage)
    const token = localStorage.getItem('authToken');
    const userData = localStorage.getItem('userData');
    
    if (token && userData) {
      setIsAuthenticated(true);
      setUser(JSON.parse(userData));
    }
    
    setLoading(false);
  }, []);

    /** LOGIN */
  const login = async (email, password) => {
    try {
      const res = await authApi.login({ email, password });
      localStorage.setItem("authToken", res.token);
      localStorage.setItem("userData", JSON.stringify(res.user));
      setIsAuthenticated(true);
      setUser(res.user);
      return { success: true, user: res.user };
    } catch (error) {
      return { success: false, error: error.message || "Login failed" };
    }
  };

  const signup = async (dto) => {
    try {
      const res = await authApi.signup(dto);
      // don’t auto-login until email is verified
      localStorage.setItem("userData", JSON.stringify({ email: dto.email }));
      setIsAuthenticated(false);
      setUser({ email: dto.email });
      return { success: true };
    } catch (error) {
      return { success: false, error: error.message || "Signup failed" };
    }
  };

  /** VERIFY EMAIL */
  const verifyEmail = async (otp) => {
    try {
      const res = await authApi.verifyEmail({ otp });
      return { success: true, ...res };
    } catch (error) {
      return { success: false, error: error.message || "Verification failed" };
    }
  };

  /** CREATE PASSWORD (after verification) */
  const createPassword = async (dto) => {
    try {
      const res = await authApi.createPassword(dto);
      return { success: true, ...res };
    } catch (error) {
      return { success: false, error: error.message || "Failed to create password" };
    }
  };

  /** FORGOT PASSWORD */
  const forgotPassword = async (email) => {
    try {
      const res = await authApi.forgotPassword({ email });
      return { success: true, ...res };
    } catch (error) {
      return { success: false, error: error.message || "Failed to send reset link" };
    }
  };

  /** RESET PASSWORD */
  const resetPassword = async (dto) => {
    try {
      const res = await authApi.resetPassword(dto);
      return { success: true, ...res };
    } catch (error) {
      return { success: false, error: error.message || "Failed to reset password" };
    }
  };

  /** CREATE PIN */
  const createPin = async (dto) => {
    try {
      const res = await authApi.createPin(dto);
      return { success: true, ...res };
    } catch (error) {
      return { success: false, error: error.message || "Failed to create pin" };
    }
  };

  const logout = () => {
    localStorage.removeItem('authToken');
    localStorage.removeItem('userData');
    setIsAuthenticated(false);
    setUser(null);
  };

  const value = {
    isAuthenticated,
    user,
    login,
    signup,
    logout,
    loading
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
