// src/api/authApi.js
import api from './axios';

export const authApi = {
  signup: (dto) => api.post('/signup', dto).then((r) => r.data),
  verifyEmail: (dto) => api.post('/verify-email', dto).then((r) => r.data),
  resendCode: (dto) => api.post('/resend-code', dto).then((r) => r.data),
  createPassword: (token, password) =>
    api.post('/create-password', { token, password }).then((r) => r.data),
  login: (dto) => api.post('/login', dto).then((r) => r.data),
  forgotPassword: (dto) => api.post('/forgot-password', dto).then((r) => r.data),
  resetPassword: (token, password) =>
    api.post('/reset-password', { token, password }).then((r) => r.data),
  createPin: (pinDto) => api.post('/create-pin', pinDto).then((r) => r.data),
};
